var simpleAdd = 'class'.create();

simpleAdd.prototype = {

initialize:function(n){
	gs.info('creating new object');
	this.number = n-0 || 0;
	},

increment : function(){

		this.number ++;
		gs.info(this.number);
		return this;
		},
	type:'simpleAdd'
};

